mapper.select:
SELECT *
FROM srtp_dot
WHERE x = #{x}
  and y = #{y}

SELECT *
FROM srtp_dotdis
WHERE m1 = #{m1}
  and n1 = #{n1}
  and m2 = #{m2}
  and n2 = #{n2}

mapper.update:
UPDATE srtp_dot


        shelves = #{shelves}



where x = #{x} and y = #{y}

update srtp_dotdis


        dis=#{dis}


where m1 = #{m1} and n1 = #{n1} and m2 = #{m2} and n2 = #{n2}
